#pragma config(Sensor, dgtl1,  bumperSwitch,   sensorTouch)
#pragma config(Sensor, dgtl2,  limitSwitch,    sensorTouch)
#pragma config(Motor,  port1,           backLeft,      tmotorVex393_HBridge, openLoop, reversed)
#pragma config(Motor,  port10,          backRight,     tmotorVex393_HBridge, openLoop, reversed)

//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//
task main()
{
	while(1==1){
	stopMotor(backLeft);
		stopMotor(backRight);
	untilBump(bumperSwitch);
		startMotor(backLeft, 15);
		startMotor(backRight, 21);
	wait(11);
		stopMotor(backLeft);
		stopMotor(backRight);}

}
